mapper.select:
select * from member,POSITION,DEPARTMENT where mem_position_number = position_number and mem_dept_number = dept_number and mem_retirement = '0'
and mem_name = #{mem_name}
and mem_dept_number = #{mem_dept_number}

mapper.update:
update member
set MEM_RETIREMENT = '1'
where mem_id = #{mem_id}
